    private void cancelBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cancelBtnActionPerformed
        // Exit application
        if(Utils.showDialog("Are you sure you want to exit?") == 0) System.exit(0);
    }//GEN-LAST:event_cancelBtnActionPerformed

    private void registerLabelMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_registerLabelMouseClicked
        // Open registration page
        new RegistrationView().setVisible(true);
    }//GEN-LAST:event_registerLabelMouseClicked

    private void loginBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_loginBtnActionPerformed
        // Login the user
        if(dataValid() && Utils.login(loginDetails)) {
            //close the login form and show main app Frame
            java.awt.EventQueue.invokeLater(() -> {
                new HomeView().setVisible(true);
            });
            this.dispose();
        }
        
    }//GEN-LAST:event_loginBtnActionPerformed

    private void passTxtActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_passTxtActionPerformed
        // Call login action if user presses enter key in password field
        this.loginBtnActionPerformed(evt);
    }//GEN-LAST:event_passTxtActionPerformed

    private boolean dataValid() {
        loginDetails[0] = usernameTxt.getText().trim();
        loginDetails[1] = Arrays.toString(passTxt.getPassword());
        if (loginDetails[0].equals("") || loginDetails[1].equals("") || loginDetails[1].isEmpty()) {
            Utils.showDialog("Enter a value for each field", "Error");
            return false;
        }
        
        return true;
    }
    
